package com.samsung.android.meerkat;

import android.content.ContentProvider;
import android.content.ContentValues;
import android.content.Context;
import android.content.SharedPreferences;
import android.database.Cursor;
import android.net.Uri;
import android.util.Log;

import java.util.Set;

public class CapabilityProvider extends ContentProvider {
    private static final String TAG = "CapabilityProvider";
    @Override
    public boolean onCreate() {
        return true;
    }

    @Override
    public Cursor query(Uri uri, String[] projection, String selection, String[] selectionArgs, String sortOrder) {
        return null;
    }

    @Override
    public String getType(Uri uri) {
        return null;
    }

    @Override
    public Uri insert(Uri uri, ContentValues values) {
        for (String key : values.keySet()) {
            Log.i(TAG, "insert key : " + key + ", value : " + values.getAsString(key));
            SharedPreferences prefs = getContext().getApplicationContext().getSharedPreferences(
                    "com.samsung.android.meerkat.CAPABILITY", Context.MODE_PRIVATE);
            prefs.edit().putString(key, values.getAsString(key)).commit();
        }
        return null;
    }

    @Override
    public int delete(Uri uri, String selection, String[] selectionArgs) {
        return 0;
    }

    @Override
    public int update(Uri uri, ContentValues values, String selection, String[] selectionArgs) {
        return 0;
    }
}
